import React, { useState } from 'react';
import { Paper, TextField, Container, Radio, FormControlLabel, Button } from '@mui/material';
import { styled } from '@mui/system';

const StyledPaper = styled(Paper)`
  padding: 20px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.4);
  margin-top: 20px;
`;

const Create2 = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [age, setAge] = useState(18);
  const [gender, setGender] = useState('');
  const [currentData, setCurrentData] = useState(null);

  const handleAddData = () => {
    const newData = {
      name,
      email,
      age,
      gender
    };

    setCurrentData(newData);

    setName('');
    setEmail('');
    setAge(18);
    setGender('');
  };

  const handleAgeChange = (e) => {
    const newAge = parseInt(e.target.value, 10);
    setAge(newAge < 18 ? 18 : newAge);
  };

  return (
    <Container maxWidth="sm">
      <StyledPaper>
        <TextField
          label="Name"
          variant="outlined"
          fullWidth
          margin="normal"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <TextField
          label="Email"
          variant="outlined"
          fullWidth
          margin="normal"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <TextField
          type="number"
          label="Age"
          variant="outlined"
          fullWidth
          margin="normal"
          value={age}
          onChange={handleAgeChange}
          inputProps={{
            min: 18,
            step: 1,
          }}
        />
        <div>
          <FormControlLabel
            control={<Radio />}
            label="Male"
            value="male"
            checked={gender === 'male'}
            onChange={() => setGender('male')}
          />
          <FormControlLabel
            control={<Radio />}
            label="Female"
            value="female"
            checked={gender === 'female'}
            onChange={() => setGender('female')}
          />
        </div>
        <div>
          <Button variant="contained" size="large" onClick={handleAddData}>
            Add
          </Button>
        </div>
      </StyledPaper>

      <div>
        {currentData && (
          <div>
            <h5>Last entered da</h5>
            <p>{`Name: ${currentData.name}`}</p>
            <p>{`Email: ${currentData.email}`}</p>
            <p>{`Age: ${currentData.age}`}</p>
            <p>{`Gender: ${currentData.gender}`}</p>
            <hr />
          </div>
        )}
      </div>
    </Container>
  );
};

export default Create2;
