import React, { useState } from 'react';
import { Paper, TextField, Container, Radio, FormControlLabel, Button } from '@mui/material';
import { styled } from '@mui/system';

const StyledPaper = styled(Paper)`
  padding: 20px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.4);
  margin-top: 20px;
`;

const Create2 = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [gender, setGender] = useState('');

  const handleAddData = () => {
    // Add the logic to store the data in another div or state variable
    // For this example, we are simply logging the entered data to the console
    console.log({
      firstName,
      lastName,
      email,
      gender
    });

    // Clear the input fields after adding the data
    setFirstName('');
    setLastName('');
    setEmail('');
    setGender('');
  };

  return (
    <Container maxWidth="sm">
      <StyledPaper>
        <TextField
          label="First Name"
          variant="outlined"
          fullWidth
          margin="normal"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <TextField
          label="Last Name"
          variant="outlined"
          fullWidth
          margin="normal"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <TextField
          label="Email"
          variant="outlined"
          fullWidth
          margin="normal"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <FormControlLabel
          control={<Radio />}
          label="Male"
          value="male"
          checked={gender === 'male'}
          onChange={() => setGender('male')}
        />
        <FormControlLabel
          control={<Radio />}
          label="Female"
          value="female"
          checked={gender === 'female'}
          onChange={() => setGender('female')}
        />
        <Button variant="contained" onClick={handleAddData}>Add</Button>
      </StyledPaper>
    </Container>
  );
};

export default Create2;
