import React, { useState } from 'react';
import { Paper, TextField, Container, Radio, FormControlLabel, Button, Fab } from '@mui/material';
import { styled } from '@mui/system';

import EditIcon from '@mui/icons-material/Edit';

const StyledPaper = styled(Paper)`
  padding: 20px;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.4);
  margin-top: 20px;
`;

const StyledFab = styled(Fab)`
  position: fixed;
  bottom: 20px;
  right: 20px;
`;

const Create2 = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [age, setAge] = useState(18);
  const [gender, setGender] = useState('');
  const [currentData, setCurrentData] = useState(null);
  const [isEditMode, setIsEditMode] = useState(false);

  const handleAddData = () => {
    const newData = {
      name,
      email,
      age,
      gender
    };

    setCurrentData(newData);

    setName('');
    setEmail('');
    setAge(18);
    setGender('');
  };

  const handleEdit = () => {
    setIsEditMode(true);
  };

  const handleSave = () => {
    setIsEditMode(false);
  };

  const handleCancel = () => {
    setIsEditMode(false);
  };

  const handleAgeChange = (e) => {
    const newAge = parseInt(e.target.value, 10);
    setAge(newAge < 18 ? 18 : newAge);
  };

  const handleEditData = () => {
    setName(currentData.name);
    setEmail(currentData.email);
    setAge(currentData.age);
    setGender(currentData.gender);
    setIsEditMode(true);
  };

  return (
    <Container maxWidth="sm">
      <StyledPaper>
        <TextField
          label="Name"
          variant="outlined"
          fullWidth
          margin="normal"
          value={name}
          onChange={(e) => setName(e.target.value)}
          disabled={!isEditMode}
        />
        <TextField
          label="Email"
          variant="outlined"
          fullWidth
          margin="normal"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          disabled={!isEditMode}
        />
        <TextField
          type="number"
          label="Age"
          variant="outlined"
          fullWidth
          margin="normal"
          value={age}
          onChange={handleAgeChange}
          inputProps={{
            min: 18,
            step: 1,
          }}
          disabled={!isEditMode}
        />
        <div>
          <FormControlLabel
            control={<Radio />}
            label="Male"
            value="male"
            checked={gender === 'male'}
            onChange={() => setGender('male')}
            disabled={!isEditMode}
          />
          <FormControlLabel
            control={<Radio />}
            label="Female"
            value="female"
            checked={gender === 'female'}
            onChange={() => setGender('female')}
            disabled={!isEditMode}
          />
        </div>
        <div>
          {isEditMode ? (
            <>
              <Button variant="contained" size="large" onClick={handleSave}>
                Save
              </Button>
              <Button variant="outlined" size="large" onClick={handleCancel}>
                Cancel
              </Button>
            </>
          ) : (
            <Button variant="contained" size="large" onClick={handleEdit}>
              Edit
            </Button>
          )}
        </div>
      </StyledPaper>

      <div>
        {currentData ? (
          <div>
            <p>{`Name: ${currentData.name}`}</p>
            <p>{`Email: ${currentData.email}`}</p>
            <p>{`Age: ${currentData.age}`}</p>
            <p>{`Gender: ${currentData.gender}`}</p>
            {!isEditMode && (
              <Button variant="outlined" size="small" onClick={handleEditData}>
                Edit
              </Button>
            )}
            <hr />
          </div>
        ) : null}
      </div>
      {currentData && !isEditMode && (
      <StyledFab color="secondary" aria-label="edit" onClick={handleEditData}>
      <EditOutlined />
    </StyledFab>

      )}
    </Container>
  );
};

export default Create2;
