import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { createUser } from "../features/userDetailSlice";
import { useNavigate } from "react-router-dom";
import { Container, Form, Button, Toast } from "react-bootstrap";

const Create = () => {
  const [users, setUsers] = useState({});
  const [showToast, setShowToast] = useState(false);
  const [isMale, setIsMale] = useState(true);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const getUserData = (e) => {
    setUsers({ ...users, [e.target.name]: e.target.value });
    console.log("typing...",users)
  };




  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createUser(users));
    setShowToast(true);
    setUsers({});

    setIsMale(!isMale);


  };

  const handleNavigateRead = () => {
    navigate("/read");
  };

  const handleToastClose = () => {
    setShowToast(false);
  };

  return (
    <Container className="d-flex justify-content-center mt-3">
      <div className="p-3 shadow">
        <h5 className="text-center mb-2">Add User</h5>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-2" controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              name="name"
              value={users.name || ""}
              onChange={getUserData}
              required
            />
          </Form.Group>
          <Form.Group className="mb-2" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={users.email || ""}
              onChange={getUserData}
              required
            />
          </Form.Group>
          <Form.Group className="mb-2" controlId="age">
            <Form.Label>Age</Form.Label>
            <Form.Control
              type="number"
              name="age"
              value={users.age || ""}
              onChange={getUserData}
              required
            />
          </Form.Group>
          {/* {/* <Form.Group className="mb-2" controlId="gender">
            <Form.Label>Gender</Form.Label>
            <Form.Control
              type="text"
              name="gender"
              value={users.gender || ""}
              onChange={getUserData}
              required
            />
          </Form.Group> */}
           */}
          <div className="d-flex justify-content-between">
            <Button type="submit" variant="primary">
              Add
            </Button>
            <Button variant="secondary" onClick={handleNavigateRead}>
              Go to Read Page
            </Button>
          </div>
        </Form>
      </div>
      <Toast
        show={showToast}
        onClose={handleToastClose}
        delay={3000}
        autohide
        style={{
          position: "absolute",
          top: "20px",
          right: "20px",
          minWidth: "200px",
        }}
      >
        <Toast.Header>
          <strong className="me-auto">Success</strong>
        </Toast.Header>
        <Toast.Body>User added successfully!</Toast.Body>
      </Toast>
    </Container>
  );
};

export default Create;
