import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { createUser } from "../features/userDetailSlice";
import { useNavigate } from "react-router-dom";
import { Container, Form, Button } from "react-bootstrap";

const Create = () => {
  const [users, setUsers] = useState({});
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const getUserData = (e) => {
    setUsers({ ...users, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createUser(users));
    navigate("/read");
  };

  return (
    <Container className="d-flex justify-content-center mt-3">
      <div className="p-3">
        <h5 className="text-center mb-2">Add User</h5>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              name="name"
              value={users.name || ""}
              onChange={getUserData}
              required
            />
            <Form.Label className={users.name ? "floating-label" : ""}>
              Name
            </Form.Label>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Control
              type="email"
              name="email"
              value={users.email || ""}
              onChange={getUserData}
              required
            />
            <Form.Label className={users.email ? "floating-label" : ""}>
              Email
            </Form.Label>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Control
              type="number"
              name="age"
              value={users.age || ""}
              onChange={getUserData}
              required
            />
            <Form.Label className={users.age ? "floating-label" : ""}>
              Age
            </Form.Label>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              name="gender"
              value={users.gender || ""}
              onChange={getUserData}
              required
            />
            <Form.Label className={users.gender ? "floating-label" : ""}>
              Gender
            </Form.Label>
          </Form.Group>
          <Button type="submit" variant="primary">
            Add
          </Button>
        </Form>
      </div>
    </Container>
  );
};

export default Create;
