import { useDispatch } from "react-redux";
import { createUser } from "../features/userDetailSlice";
import { useNavigate } from "react-router-dom";
import { Container, Form, Button, Toast } from "react-bootstrap";
import Switch from "react-switch";

const Create = () => {
  const [users, setUsers] = useState({});
  const [showToast, setShowToast] = useState(false);
  const [isMale, setIsMale] = useState(false); // New state for gender selection

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const getUserData = (e) => {
    setUsers({ ...users, [e.target.name]: e.target.value });
    console.log("typing...", users);
  };

  const handleGenderChange = (checked) => {
    setIsMale(checked);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const gender = isMale ? "Male" : "Female";
    dispatch(createUser({ ...users, gender }));
    setShowToast(true);
    setUsers({});
    setIsMale(false); // Reset gender selection to female after submission
  };

  const handleNavigateRead = () => {
    navigate("/read");
  };

  const handleToastClose = () => {
    setShowToast(false);
  };

  return (
    <Container className="d-flex justify-content-center mt-3">
      <div className="p-3 shadow">
        <h5 className="text-center mb-2">Add User</h5>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-2" controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              name="name"
              value={users.name || ""}
              onChange={getUserData}
              required
            />
          </Form.Group>
          <Form.Group className="mb-2" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={users.email || ""}
              onChange={getUserData}
              required
            />
          </Form.Group>
          <Form.Group className="mb-2" controlId="age">
            <Form.Label>Age</Form.Label>
            <Form.Control
              type="number"
              name="age"
              value={users.age || ""}
              onChange={getUserData}
              required
            />
          </Form.Group>
          <Form.Group className="mb-2" controlId="gender">
            <Form.Label>Gender</Form.Label>
            <div>
              <Switch
                checked={isMale}
                onChange={handleGenderChange}
                onColor="#86d3ff"
                onHandleColor="#2693e6"
                handleDiameter={20}
                uncheckedIcon={false}
                checkedIcon={false}
                boxShadow="0px 1px 5px rgba(0, 0, 0, 0.6)"
                activeBoxShadow="0px 1px 5px rgba(0, 0, 0, 0.6)"
                height={15}
                width={40}
              />
              <span style={{ marginLeft: "10px" }}>
                {isMale ? "Male" : "Female"}
              </span>
            </div>
          </Form.Group>
          <div className="d-flex justify-content-between">
            <Button type="submit" variant="primary">
              Add
            </Button>
            <Button variant="secondary" onClick={handleNavigateRead}>
              Go to Read Page
            </Button>
          </div>
        </Form>
      </div>
      <Toast
        show={showToast}
        onClose={handleToastClose}
        delay={3000}
        autohide
        style={{
          position: "absolute",
          top: "20px",
          right: "20px",
          minWidth: "200px",
        }}
      >
        <Toast.Header>
          <strong className="me-auto">Success</strong>
        </Toast.Header>
        <Toast.Body>User added successfully!</Toast.Body>
      </Toast>
    </Container>
  );
};

export default Create;
